INSERT INTO number_associations (number, hero, action, object, explanation, is_primary, rating, total_votes, created_at) VALUES
(0, 'Myszka Miki', 'uderza', 'Piłka', 'Magiczny krąg, stworzony przez czarodzieja Merlina, reprezentuje liczbę 0. Ma okrągły kształt, podobny do tej liczby, a także symbolizuje początek i koniec, co jest zgodne z koncepcją liczby 0.', true, 0, 0, '2025-10-04 15:03:07.273582'),
(1, 'Kubuś Puchatek', 'dotyka', 'Klucz', 'Smerf podnosi jedno lody, które wyglądają jak liczba 1. Jest to zabawne, bo lody są większe niż Smerf!', true, 0, 0, '2025-10-04 18:18:49.191105'),
(3, 'Tygrysek', 'pcha', 'Telefon', 'Scooby-Doo szuka trzech lizaków, które są tak słodkie jak liczba 3!', true, 0, 0, '2025-10-04 18:22:33.530269'),
(6, 'Nala z Króla Lwa', 'szepcze', 'Mleko', 'Winnie the Pooh zawsze jest głodny i uwielbia miód. Wyobraź sobie, jak siedzi i zjada 6 słoików miodu. Kształt słoika miodu przypomina kształt liczby 6.', true, 0, 0, '2025-10-04 18:19:23.05549'),
(9, 'Pumba', 'tańczy', 'Lampa', 'Cerberus, trzygłowy pies strzegący Hadesu, tutaj strzeże dziewięciogłowego smoka, symbolizując liczbę 9. W mitologii, dziewięć często kojarzone jest z wielkością i potęgą, a także z cyklicznością i odrodzeniem.', true, 0, 0, '2025-10-04 15:03:30.316541'),
(11, 'Anna z Krainy Lodu', 'rysuje', 'Czapka', 'Tom i Jerry stoją między dwoma słupami, które przypominają kształt liczby 11.', true, 0, 0, '2025-10-04 18:19:42.717342'),
(12, 'Olaf', 'maluje', 'Okulary', 'Herkules, znany z mitologii greckiej, musiał wykonać 12 prac jako pokuta za swoje czyny. Prace te symbolizują ogromne zadanie, które wymaga siły, odwagi i zdolności do pokonania wyzwań, co jest metaforą liczby 12 - liczby pełnej i kompleksowej.', true, 0, 0, '2025-10-04 15:03:40.6095'),
(13, 'Kristoff', 'lepi', 'Ołówek', 'Spongebob, znany z miłości do gotowania, robi 13 pysznych pączków, które są ułożone tak, że wyglądają jak liczba 13.', true, 0, 0, '2025-10-04 17:57:05.775031'),
(14, 'Sven', 'głaszcze', 'Szczotka', 'Spiderman skacze na trampolinie 14 razy, w górę i w dół, przypominając kształt liczby 14', true, 0, 0, '2025-10-04 17:57:14.405616'),
(15, 'Ariel z Małej Syrenki', 'puszcza', 'Lustro', 'Harry Potter, znany młody czarodziej, siedzi w Hogwarcie i odczytuje 15 magicznych zaklęć z książki. Liczba 15 przypomina kształt magicznej różdżki, której używa Harry do rzucania zaklęć.', true, 0, 0, '2025-10-04 18:33:35.252571'),
(16, 'Sebastian z Małej Syrenki', 'patrzy', 'Zamek', 'Królowa Elżbieta I wstąpiła na tron Anglii w wieku 16 lat, co symbolizuje przejście do dorosłości, siłę i autorytet liczby 16.', true, 0, 0, '2025-10-04 17:54:15.714405'),
(17, 'Florek (rybka z Małej Syrenki)', 'wącha', 'Wiadro', 'Elsa używa swojej magii, aby zbudować siedemnaście bałwanów, które tańczą i bawią się na śniegu. Każdy bałwan wygląda jak cyfra 1 lub 7, tworząc razem liczbę 17!', true, 0, 0, '2025-10-04 18:23:06.691252'),
(18, 'Złotowłosa', 'słucha', 'Łyżka', 'Zeus, jako władca bóg Olimpu, jest znany z rzucających błyskawice, które są symbolem jego potęgi. Liczba 18 jest często kojarzona z pełnoletniością, dorosłością i dojrzewaniem, co może być metaforą dla potężnej i dojrzewającej siły Zeusa.', true, 0, 0, '2025-10-04 17:54:38.148895'),
(19, 'Kopciuszek', 'ogląda', 'Talerz', 'Robin Hood, znany ze swojej precyzji i umiejętności strzelania, strzela 19 strzałami w cel, co symbolizuje pełnię, kompletność i celność liczby 19.', true, 0, 0, '2025-10-04 17:54:41.558715'),
(20, 'Śpiąca Królewna', 'śmieje', 'Gitara', 'Myszka Miki, ulubiona postać wszystkich dzieci, karmi 20 małych, zielonych żółwi. Każdy żółw jest jak mały okrągły ''0'', a Myszka Miki jest jak duża ''2'', razem tworząc liczbę 20.', true, 0, 0, '2025-10-04 18:17:22.955534'),
(21, 'Królewna Śnieżka', 'płacze', 'Aparat fotograficzny', 'Cesarz Nero jest znany z tego, że grał na lutni podczas wielkiego pożaru Rzymu. Tutaj, 21-strunowa lutnia symbolizuje liczby 21, podkreślając bogactwo i różnorodność dźwięków, które można na niej wydobyć, co jest metaforą dla różnorodności i bogactwa doświadczeń, które mogą nadejść po osiągnięciu pełnej dorosłości w wieku 21 lat.', true, 0, 0, '2025-10-04 17:54:51.851328'),
(24, 'Bestia', 'kaszle', 'Młotek', 'Jack Bauer, bohater serialu ''24'', jest znanym postaciem, która ma 24 godziny na rozwiązanie zagadki. Zegar jest tu symbolem tych 24 godzin.', true, 0, 0, '2025-10-04 17:55:00.544236'),
(25, 'Alicja z Krainy Czarów', 'ziewa', 'Nóż', 'Królewna Śnieżka tańczy z 25 krasnoludkami. Bawią się tak dobrze, że wygląda to jak wesołe 2 i 5 w ruchu!', true, 0, 0, '2025-10-04 18:21:21.338229'),
(28, 'Królowa Kier', 'gryzie', 'Świeca', 'Pikachu jest dobrze znanym Pokemonem, który potrafi łapać błyskawice. Dzieci mogą sobie wyobrazić, jak Pikachu łapie 28 błyskawic, przypominając sobie liczbę 28.', true, 0, 0, '2025-10-04 18:33:40.450511'),
(29, 'Pinokio', 'liże', 'Kwiat', 'Kaczor Donald, znany ze swojego kapelusza marynarza i charakterystycznego kaczego mówienia, jest teraz super tajnym agentem! W swojej najnowszej misji, szyfruje wiadomości do swoich przyjaciół na 29 kolorowych balonach. Te balony są rozmieszczone w taki sposób, że przypominają kształt liczby 29. To jest zabawne i łatwe do zapamiętania skojarzenie dla dzieci.', true, 0, 0, '2025-10-04 18:32:20.301018'),
(30, 'Geppetto', 'żuje', 'Balon', 'Księżniczka Roszpunka ma bardzo długie, złote włosy. W naszym skojarzeniu czesze 30 z nich, co pomaga dzieciom visualizować liczbę 30.', true, 0, 0, '2025-10-04 18:33:44.439373'),
(31, 'Dżepetto', 'połyka', 'Ryba', 'Pingwiny z Madagaskaru są znane z ich zabawnych i kreatywnych pomysłów. W naszym skojarzeniu, organizują oni 31 urodziny dla ich przyjaciela, króla Juliana. Liczba 31 przypomina kształt pingwina z boku z dużym brzuchem (3) i małą głową (1). To skojarzenie jest zabawne, kolorowe i łatwe do zapamiętania dla dzieci.', true, 0, 0, '2025-10-04 18:33:49.049339'),
(32, 'Jiminy Świerszcz', 'smakuje', 'Samolot', 'SpongeBob SquarePants jest mistrzem grillowania! Wyobraź sobie, jak przyrządza 32 pyszne klopsiki na swoim grillu. Każdy klopsik reprezentuje jedną jednostkę liczby 32.', true, 0, 0, '2025-10-04 17:57:56.395144'),
(33, 'Bambi', 'je', 'Samochód', 'Elsa z ''Krainy Lodu'' śpiewa, tworząc 33 śnieżki. Śnieżki są białe i okrągłe, przypominające kształt liczby 3, a jest ich dokładnie 33.', true, 0, 0, '2025-10-04 18:24:32.280616'),
(34, 'Thumper (Kłapouchy królik z Bambi)', 'pije', 'Statek', 'Smerf Maruda, znanego z niezadowolonego wyrazu twarzy, wyobraź sobie jak siedzi i liczy 34 marchewki. Liczba 3 może wyglądać jak dwie marchewki leżące obok siebie, a liczba 4 może wyglądać jak marchewka z liśćmi na górze.', true, 0, 0, '2025-10-04 18:24:42.109622'),
(35, 'Dumbo', 'smaży', 'Szalik', 'Garfield, słynny pomarańczowy kot z kreskówki, uwielbia jeść lasagne. Możemy go łatwo wyobrazić sobie, jak zjada 35 kawałków lasagne, co pomaga zapamiętać liczbę 35.', true, 0, 0, '2025-10-04 18:33:51.813446'),
(36, 'Myszka Minnie', 'gotuje', 'List', 'Stitch z filmu ''Lilo&Stitch'' jest znanym surferem. Możemy go wyobrazić sobie surfującego na 36 różnokolorowych falach, co pomaga dzieciom zapamiętać liczbę 36.', true, 0, 0, '2025-10-04 18:33:57.032811'),
(37, 'Kaczor Donald', 'piecze', 'Portfel', 'Kot w Butach gra na skrzypcach, które mają 3 struny (jak trójka w 37). Podczas gry, Kot w Butach tańczy w rytm muzyki, a jego ogon tworzy kształt 7 (jak siódemka w 37). To zabawne i kolorowe skojarzenie pomaga dzieciom przypomnieć sobie liczbę 37.', true, 0, 0, '2025-10-04 18:29:36.544025'),
(38, 'Kaczka Daisy', 'miesza', 'Kostka Rubika', 'Rozważmy króla Lwa Simbę, który troskliwie opiekuje się 38 młodymi lwiątkami. Kształt 3 przypomina głowę lwa, a 8 wygląda jak dwie małe lwiątka bawiące się ze sobą. Dzieci mogą sobie wyobrazić, jak troskliwy Król Lew Simba dba o swoje małe lwiątka, co pomoże im zapamiętać liczbę 38.', true, 0, 0, '2025-10-04 18:34:02.364289'),
(39, 'Goofy', 'waży', 'Mydło', 'Księżniczka Ariel, znana z ''Małej Syrenki'', bawi się na morskim dnie i odkrywa 39 perł. Każda z nich świeci tak jasno, jak liczba 39 w naszych umysłach!', true, 0, 0, '2025-10-04 18:34:05.380239'),
(40, 'Pluto', 'mierzy', 'Lody', 'Dinozaur Rex z Toy Story używa swojego długiego ogona jak łopaty, aby kopać 40 dziur w piasku. Dzieci mogą wyobrazić sobie, że każda dziura to cyfra 0, a 40 dziur to liczba 40.', true, 0, 0, '2025-10-04 18:34:11.470877'),
(41, 'Zygzak McQueen', 'pisze', 'Butelka', 'Shrek gotuje 4 duże marchewki i 1 ziemniaka do swojej ulubionej zupy, co przypomina liczbę 41.', true, 0, 0, '2025-10-04 17:59:23.998768'),
(42, 'Złomek z Aut', 'czyta', 'Brelok', 'Frodo z ''Władca Pierścieni'' jest znanym bohaterem, który jest symbolem długiej podróży. 42 mil to odległość, którą Frodo mógłby pokonać w swojej podróży, co pomaga dzieciom zapamiętać liczbę 42.', true, 0, 0, '2025-10-04 18:34:21.523094'),
(43, 'Buzz Astral z Toy Story', 'mówi', 'Pasta do zębów', 'Król Julian z Madagaskaru gra na wielkich skrzypcach, które mają 43 struny. Wizualizacja tej sceny pomaga dzieciom zapamiętać liczbę 43.', true, 0, 0, '2025-10-04 18:34:28.961075'),
(44, 'Woody z Toy Story', 'pyta', 'Tort', 'Batman jeździ swoim batmobil, który ma 4 koła z przodu i 4 koła z tyłu, co przypomina nam liczbę 44.', true, 0, 0, '2025-10-04 17:59:34.00473'),
(45, 'Jessie z Toy Story', 'odpowiada', 'Dzwonek', 'Czarodziejka Sabrina odczarowuje 45 żab, które niespodziewanie zamieniły się w książąt. Odczarowanie tylu żab to wielkie wyzwanie, ale Sabrina jest na to gotowa. Dzieci mogą łatwo zapamiętać tę liczbę, wyobrażając sobie, jak Sabrina wyczarowuje swoje zaklęcie, a potem jedna po drugiej żaby zamieniają się z powrotem w książąt.', true, 0, 0, '2025-10-04 18:34:35.433844'),
(46, 'Rex z Toy Story', 'opowiada', 'Zdjęcie', 'Scooby Doo jada 46 hotdogów na wielkim konkursie jedzenia. 4 jest jak bułka hotdoga, a 6 jak kiełbasa w środku!', true, 0, 0, '2025-10-04 17:59:40.926767'),
(47, 'Mr. Potato Head', 'słodzi', 'Moneta', 'Bart Simpson z kreskówki ''Simpsonowie'' jest znany z miłości do rysowania. Możemy go sobie wyobrazić, jak rysuje 47 komiksów, które tworzą kształt liczby 47.', true, 0, 0, '2025-10-04 18:34:38.737673'),
(48, 'Barbie z Toy Story', 'soli', 'Słońce', 'Dora, znana z miłości do przyrody, rozmawia z 48 kolorowymi motylami. Każdy motyl reprezentuje jeden punkt na mapie, której używa Dora do swoich przygód.', true, 0, 0, '2025-10-04 18:34:46.503346'),
(49, 'Forky', 'kroi', 'Chmura', 'Pocahontas, znana z jej umiejętności poruszania się z naturą, płynie na rzece na 49 liściach, co przypomina kształt liczby 49. Dzieci mogą łatwo sobie wyobrazić tę scenę, ponieważ Pocahontas często jest pokazywana podczas interakcji z elementami natury.', true, 0, 0, '2025-10-04 18:34:51.525638'),
(50, 'SpongeBob Kanciastoporty', 'obiera', 'Dom', 'Bugs Bunny, znany królik z kreskówek, gra na fladze melodyjnie, a na tej fladze są 50 nut. To jest nasze kolorowe i zabawne skojarzenie dla liczby 50.', true, 0, 0, '2025-10-04 18:34:55.406838'),
(51, 'Patryk Rozgwiazda', 'pakuje', 'Drzwi', 'Pippi Pończoszanka jest niesamowicie silna i zwinna, więc dla niej wejście na drabinę z 51 szczebelkami to żaden problem. Liczba 51 przypomina drabinę z 5 dużymi szczebelkami na dole (5) i 1 szczebelek na górze (1).', true, 0, 0, '2025-10-04 18:35:01.235626'),
(52, 'Pan Krab', 'otwiera', 'Serce', 'Marta z bajki ''Marta Mówi'' jest inteligentną i ciekawą świata psicą. Dzieci często widzą Martę z książką, więc łatwo im będzie zapamiętać, że Marta czyta 52 książki. Można to łączyć z obrazem dużej biblioteki, w której Marta siedzi i czyta.', true, 0, 0, '2025-10-04 18:35:14.448039'),
(53, 'Skalmarek', 'zamyka', 'Gwóźdź', 'Mulan, odważna bohaterka znanego filmu, używa swojej mądrości i siły, aby wspinać się na 53 kamienie, co przypomina kształt liczby 53. Jest to łatwe do wyobrażenia i zapamiętania dla dzieci, które mogą sobie wyobrazić Mulan, jak wspina się coraz wyżej i wyżej.', true, 0, 0, '2025-10-04 18:36:09.197807'),
(54, 'Sandy Pysia', 'naciska', 'Bateria', 'Pinkie Pie z Kucyków Pony uwielbia piec babeczki. Wyobraź sobie, jak piecze 54 babeczki, które wyglądają jak liczba 54. Każda z nich jest kolorowa i pachnąca. To jest proste i zabawne skojarzenie dla liczby 54.', true, 0, 0, '2025-10-04 18:36:18.03916'),
(55, 'Dora poznaje świat', 'kręci', 'Guzik', 'Król Lew Mufasa z dumą naucza 55 małych lwiat, jak być prawdziwymi królami, co przypomina kształt liczby 55, gdzie lwiaty tworzą obie ''piątki''.', true, 0, 0, '2025-10-04 18:36:22.621217'),
(56, 'Butek (małpka Dory)', 'obraca', 'Torebka', 'Buzz Astral z Toy Story lata rakietą, która ma kształt podobny do liczby 5, a dym za rakietą tworzy kształt podobny do liczby 6. To jest zabawne, kolorowe i łatwe do zapamiętania skojarzenie dla liczby 56.', true, 0, 0, '2025-10-04 18:01:30.92053'),
(57, 'Diego (kuzyn Dory)', 'przyciska', 'Pióro', 'Złotowłosa, znana z bajki o trzech niedźwiadkach, uwielbia rowery. Przypomnij sobie, jak wygląda cyfra 5, która przypomina kształt roweru, a 7 to liczba rowerów na której jedzie Złotowłosa. Więc 57 to Złotowłosa, która podróżuje na 57 rowerach.', true, 0, 0, '2025-10-04 18:36:34.318763'),
(58, 'Świnka Peppa', 'podnosi', 'Zeszyt', 'Lightning McQueen to szybki i zwinny samochód wyścigowy, który z łatwością przejeżdża przez 58 pomarańczowych stożków, tworząc ścieżkę przypominającą kształt liczby 58.', true, 0, 0, '2025-10-04 18:36:39.709635'),
(59, 'George (brat Peppy)', 'opuszcza', 'Mapa', 'Smerfetka, kochająca przyrodę, sadzi 59 kolorowych tulipanów w ogrodzie Smerfów. Liczba 59 tulipanów tworzy kształt podobny do liczby 59, co ułatwia dzieciom zapamiętanie.', true, 0, 0, '2025-10-04 18:36:48.420134'),
(60, 'Mama Świnka', 'siada', 'Kula do kręgli', 'Księżniczka Zelda z gry ''Legend of Zelda'' siedzi na złotym tronie i gra na wielkiej harfie, która ma 60 strun. Dźwięki, które wydobywa z niej, są tak piękne, że każdy, kto je słyszy, zapamiętuje, że harfa ma 60 strun!', true, 0, 0, '2025-10-04 18:38:49.109091'),
(61, 'Tata Świnka', 'wstaje', 'Pizza', 'Król Lew Nala jest znana i lubiana postać dla dzieci. Kąpiel w kroplach deszczu jest zabawna i łatwa do wyobrażenia. Liczba 61 przypomina kształt kropel deszczu spadającego z nieba.', true, 0, 0, '2025-10-04 18:38:53.664077'),
(62, 'Rebeka Królik', 'biegnie', 'Piłeczka tenisowa', 'Król Lew Rafiki maluje 62 banany na ścianie swojej jaskini. Wizualizacja 62 bananów pomoże dzieciom zapamiętać numer.', true, 0, 0, '2025-10-04 18:39:01.15534'),
(63, 'Suzy Owca', 'idzie', 'Pianino', 'Woody z Toy Story składa 63 klocki Lego, tworząc kolorową wieżę. Jest to łatwe do zapamiętania, ponieważ wieża przypomina kształt liczby 63.', true, 0, 0, '2025-10-04 18:39:04.812115'),
(64, 'Marta z Marta mówi', 'skacze', 'Deskorolka', 'Kubuś Puchatek zamawia 64 miodowe ciasteczka, bo jest bardzo głodny. Wizualizacja liczby 64 może być łatwa, jeśli wyobrażamy sobie stół pełen ciasteczek. 6 ciasteczek ułożonych w kształt ''6'' i 4 ciasteczka ułożone w kształt ''4''.', true, 0, 0, '2025-10-04 18:39:11.607167'),
(65, 'Franklin żółw', 'spada', 'Pilot', 'Olaf, sympatyczny bałwan z ''Krainy Lodu'', topi 65 kulek śniegu. To skojarzenie jest łatwe do zapamiętania, ponieważ Olaf jest bałwanem i topienie śniegu jest dla niego naturalne. Liczba 65 może przypominać kształt kulek śniegu, które topi Olaf.', true, 0, 0, '2025-10-04 18:39:23.294776'),
(66, 'Mała Mi z Muminków', 'wspina', 'Bałwan', 'Król Julian tańczy z 66 lemurkami w rytm muzyki. Każdy lemurek to małe kółko, a razem tworzą liczbę 66!', true, 0, 0, '2025-10-04 18:51:49.564299'),
(67, 'Muminek', 'zjeżdża', 'Latarka', 'Myszka Miki rysuje 67 serduszek dla Minnie. Każde serduszko to małe kółko, a razem tworzą liczbę 67!', true, 0, 0, '2025-10-04 18:51:49.564299'),
(68, 'Panna Migotka', 'leci', 'Motyl', 'Kubuś Puchatek liczy 68 słoików miodu w swojej spiżarni. Każdy słoik to małe kółko, a razem tworzą liczbę 68!', true, 0, 0, '2025-10-04 18:51:49.564299'),
(69, 'Ryjek', 'pływa', 'Banan', 'Nemo, mała, pomarańczowa rybka pływa i puszcza 69 kolorowych baniek mydlanych. Bąbelki unoszą się w wodzie tworząc kształt liczby 69.', true, 0, 0, '2025-10-04 18:43:02.006977'),
(70, 'Włóczykij', 'wiosłuje', 'Medal', 'Tarzan, znany z huśtania się na lianach, przeskakuje z jednej na drugą. Wyobraź sobie, jak skacze po 70 lianach, co przypomina kształt liczby 70!', true, 0, 0, '2025-10-04 18:40:59.128397'),
(71, 'Pocahontas', 'nurkue', 'Album ze zdjęciami', 'Bambi, mała sarna z kreskówki, skacze przez 71 sarenki. Dzieci mogą łatwo zapamiętać tę scenę, ponieważ Bambi jest popularną postacią, a skakanie przez sarenki jest zabawnym i kolorowym obrazem.', true, 0, 0, '2025-10-04 18:42:35.280129'),
(72, 'Mulan', 'ciągnie', 'Prezent', 'Elsa tworzy 72 płatki śniegu swoją magią. Każdy płatek to małe kółko, a razem tworzą liczbę 72!', true, 0, 0, '2025-10-04 18:51:57.664128'),
